   <script>
+    // Form validation and submission
+    class FormValidator {
+      constructor() {
+        this.form = document.getElementById('notify-form');
+        this.submitBtn = document.getElementById('submitBtn');
+        this.setupEventListeners();
+      }
+
+      setupEventListeners() {
+        this.form.addEventListener('submit', this.handleSubmit.bind(this));
+        
+        // Real-time validation
+        document.getElementById('name').addEventListener('blur', this.validateName.bind(this));
+        document.getElementById('email').addEventListener('blur', this.validateEmail.bind(this));
+        document.getElementById('interest').addEventListener('change', this.validateInterest.bind(this));
+      }
+
+      validateName() {
+        const name = document.getElementById('name').value.trim();
+        const nameError = document.getElementById('nameError');
+        const namePattern = /^[A-Za-z\s]{2,}$/;
+        
+        if (!namePattern.test(name)) {
+          nameError.style.display = 'block';
+          return false;
+        }
+        nameError.style.display = 'none';
+        return true;
+      }
+
+      validateEmail() {
+        const email = document.getElementById('email').value.trim();
+        const emailError = document.getElementById('emailError');
+        const emailPattern = /^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$/i;
+        
+        // Check for suspicious patterns
+        const suspiciousPatterns = [
+          /test@test/i,
+          /example@example/i,
+          /fake@fake/i,
+          /spam@spam/i,
+          /temp@temp/i
+        ];
+        
+        if (!emailPattern.test(email) || suspiciousPatterns.some(pattern => pattern.test(email))) {
+          emailError.style.display = 'block';
+          return false;
+        }
+        emailError.style.display = 'none';
+        return true;
+      }
+
+      validateInterest() {
+        const interest = document.getElementById('interest').value;
+        const interestError = document.getElementById('interestError');
+        
+        if (!interest) {
+          interestError.style.display = 'block';
+          return false;
+        }
+        interestError.style.display = 'none';
+        return true;
+      }
+
+      async handleSubmit(e) {
+        e.preventDefault();
+        
+        // Validate all fields
+        const isNameValid = this.validateName();
+        const isEmailValid = this.validateEmail();
+        const isInterestValid = this.validateInterest();
+        
+        if (!isNameValid || !isEmailValid || !isInterestValid) {
+          return;
+        }
+
+        // Show loading state
+        this.submitBtn.classList.add('loading');
+        this.submitBtn.disabled = true;
+
+        try {
+          // Netlify form submission
+          const formData = new FormData(this.form);
+          
+          const response = await fetch('/', {
+            method: 'POST',
+            headers: { "Content-Type": "application/x-www-form-urlencoded" },
+            body: new URLSearchParams(formData).toString()
+          });
+
+          if (response.ok) {
+            // Check launch date - redirect to main site if it's live
+            const launchDate = new Date('2025-06-07T18:00:00-04:00'); // June 7, 2025 at 6:00 PM Eastern
+            const now = new Date();
+            
+            if (now >= launchDate) {
