-  <script>
-    // Form validation and submission
-    class FormValidator {
-      constructor() {
-        this.form = document.getElementById('notify-form');
-        this.submitBtn = document.getElementById('submitBtn');
-        this.setupEventListeners();
-      }
-
-      setupEventListeners() {
-        this.form.addEventListener('submit', this.handleSubmit.bind(this));
-        
-        // Real-time validation
-        document.getElementById('name').addEventListener('blur', this.validateName.bind(this));
-        document.getElementById('email').addEventListener('blur', this.validateEmail.bind(this));
-        document.getElementById('interest').addEventListener('change', this.validateInterest.bind(this));
-      }
-
-      validateName() {
-        const name = document.getElementById('name').value.trim();
-        const nameError = document.getElementById('nameError');
-        const namePattern = /^[A-Za-z\s]{2,}$/;
-        
-        if (!namePattern.test(name)) {
-          nameError.style.display = 'block';
-          return false;
-        }
-        nameError.style.display = 'none';
-        return true;
-      }
-
-      validateEmail() {
-        const email = document.getElementById('email').value.trim();
-        const emailError = document.getElementById('emailError');
-        const emailPattern = /^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$/i;
-        
-        // Check for suspicious patterns
-        const suspiciousPatterns = [
-          /test@test/i,
-          /example@example/i,
-          /fake@fake/i,
-          /spam@spam/i,
-          /temp@temp/i
-        ];
-        
-        if (!emailPattern.test(email) || suspiciousPatterns.some(pattern => pattern.test(email))) {
-          emailError.style.display = 'block';
-          return false;
-        }
-        emailError.style.display = 'none';
-        return true;
-      }
-
-      validateInterest() {
-        const interest = document.getElementById('interest').value;
-        const interestError = document.getElementById('interestError');
-        
-        if (!interest) {
-          interestError.style.display = 'block';
-          return false;
-        }
-        interestError.style.display = 'none';
-        return true;
-      }
-
-      async handleSubmit(e) {
-        e.preventDefault();
-        
-        // Validate all fields
-        const isNameValid = this.validateName();
-        const isEmailValid = this.validateEmail();
-        const isInterestValid = this.validateInterest();
-        
-        if (!isNameValid || !isEmailValid || !isInterestValid) {
-          return;
-        }
-
-        // Show loading state
-        this.submitBtn.classList.add('loading');
-        this.submitBtn.disabled = true;
-
-        try {
-          // Netlify form submission
-          const formData = new FormData(this.form);
-          
-          const response = await fetch('/', {
-            method: 'POST',
-            headers: { "Content-Type": "application/x-www-form-urlencoded" },
-            body: new URLSearchParams(formData).toString()
-          });
-
-          if (response.ok) {
-            // Check launch date - redirect to main site if it's live
-            const launchDate = new Date('2025-06-07T18:00:00-04:00'); // June 7, 2025 at 6:00 PM Eastern
-            const now = new Date();
-            
-            if (now >= launchDate) {
-              // Site is live, redirect to main site
-              console.log('Redirecting to main site - launch date reached!');
-              window.location.href = 'main.html';
-            } else {
-              // Show thank you message
-              document.getElementById('formContainer').style.display = 'none';
-              document.getElementById('thankYou').style.display = 'flex';
-              document.getElementById('thankYou').classList.add('success-animation');
-              
-              // Analytics tracking
-              if (typeof gtag !== 'undefined') {
-                gtag('event', 'form_submit', {
-                  event_category: 'engagement',
-                  event_label: 'landing_page_signup'
-                });
-              }
-            }
-          } else {
-            throw new Error('Network response was not ok');
-          }
-        } catch (error) {
-          console.error('Form submission error:', error);
-          alert('There was an error submitting your form. Please try again.');
-        } finally {
-          this.submitBtn.classList.remove('loading');
-          this.submitBtn.disabled = false;
-        }
-      }
-    }
-
-    // Video slider functionality
-    const videoSlides = document.querySelectorAll('.video-slide');
-    const videos = document.querySelectorAll('.video-slide video');
-    let currentVideoSlide = 0;
-    let videoInterval;
-
-    function startVideoSlider() {
-      videoInterval = setInterval(() => {
-        videoSlides[currentVideoSlide].classList.remove('active');
-        videos[currentVideoSlide].pause();
-        
-        currentVideoSlide = (currentVideoSlide + 1) % videoSlides.length;
-        
-        videoSlides[currentVideoSlide].classList.add('active');
-        videos[currentVideoSlide].play().catch(e => {
-          console.log('Video autoplay prevented:', e);
-          const fallback = videos[currentVideoSlide].nextElementSibling;
-          if (fallback) fallback.style.display = 'block';
-        });
-      }, 12000); // 12 seconds per video
-    }
-
-    // Initialize videos
-    videos.forEach((video, index) => {
-      video.addEventListener('loadeddata', () => {
-        const fallback = video.nextElementSibling;
-        if (fallback) fallback.style.display = 'none';
-      });
-
-      video.addEventListener('error', () => {
-        const fallback = video.nextElementSibling;
-        if (fallback) fallback.style.display = 'block';
-      });
-    });
-
-    // Launch date logic - June 7, 2025 at 6:00 PM Eastern Time
-    const launchDate = new Date('2025-06-07T18:00:00-04:00'); // EDT (Eastern Daylight Time)
-    const mainSiteLink = document.getElementById('mainSiteLink');
-    const comingSoonBadge = document.getElementById('comingSoonBadge');
-    const announcementText = document.getElementById('announcementText');
-    const announcement = document.querySelector('.announcement');
-
-    function checkLaunchDate() {
-      const now = new Date();
-      console.log('Current time:', now.toLocaleString());
-      console.log('Launch time (Eastern):', launchDate.toLocaleString());
-      console.log('Site launched:', now >= launchDate);
-      
-      if (now >= launchDate) {
-        // Site is live - update UI
-        mainSiteLink.style.display = 'block';
-        comingSoonBadge.style.display = 'none';
-        
-        // Update announcement message with welcome
-        announcementText.textContent = 'Welcome to MeishaVernell.com • The future is here • Come on in!';
-        announcement.classList.add('live');
-        
-        console.log('Site is now live! ENTER SITE button activated.');
-      } else {
-        // Site not yet live - show coming soon
-        mainSiteLink.style.display = 'none';
-        comingSoonBadge.style.display = 'block';
-        
-        // Keep original announcement message
-        announcementText.textContent = 'Special announcement coming 6.15.25 • Thank you for being here';
-        announcement.classList.remove('live');
-        
-        // Show countdown in console for debugging
-        const timeLeft = launchDate - now;
-        const days = Math.floor(timeLeft / (1000 * 60 * 60 * 24));
-        const hours = Math.floor((timeLeft % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
-        const minutes = Math.floor((timeLeft % (1000 * 60 * 60)) / (1000 * 60));
-        console.log(`Time until launch: ${days} days, ${hours} hours, ${minutes} minutes`);
-      }
-    }
-
-    // Handle page visibility
-    document.addEventListener('visibilitychange', () => {
-      if (document.hidden) {
-        clearInterval(videoInterval);
-        videos.forEach(video => video.pause());
-      } else {
-        startVideoSlider();
-        videos[currentVideoSlide].play().catch(e => console.log('Resume failed'));
-      }
-    });
-
-    // Initialize everything
-    document.addEventListener('DOMContentLoaded', () => {
-      new FormValidator();
-      checkLaunchDate();
-      startVideoSlider();
-      
-      // Play first video
-      videos[0].play().catch(e => {
-        console.log('Initial video autoplay prevented:', e);
-        const fallback = videos[0].nextElementSibling;
-        if (fallback) fallback.style.display = 'block';
-      });
-      
-      // Check launch date every 30 seconds for more responsive updates
-      setInterval(checkLaunchDate, 30000);
-    });
-  </script>
