@@ -0,0 +1,542 @@
+/* Reset */
+* {
+  margin: 0;
+  padding: 0;
+  box-sizing: border-box;
+}
+
+body {
+  font-family: 'Arial', sans-serif;
+  overflow-x: hidden;
+}
+
+/* Video Background Styling - Full Screen */
+.video-slider {
+  position: fixed;
+  top: 0;
+  left: 0;
+  width: 100vw;
+  height: 100vh;
+  z-index: -1;
+  overflow: hidden;
+}
+
+.video-slides {
+  position: relative;
+  width: 100%;
+  height: 100%;
+}
+
+.video-slide {
+  position: absolute;
+  top: 0;
+  left: 0;
+  width: 100%;
+  height: 100%;
+  opacity: 0;
+  transition: opacity 1s ease-in-out;
+}
+
+.video-slide.active {
+  opacity: 1;
+}
+
+.video-slide video {
+  width: 100vw;
+  height: 100vh;
+  object-fit: cover;
+  object-position: center;
+}
+
+.video-fallback {
+  position: absolute;
+  top: 0;
+  left: 0;
+  width: 100%;
+  height: 100%;
+  display: none;
+}
+
+.video-overlay {
+  position: absolute;
+  top: 0;
+  left: 0;
+  width: 100%;
+  height: 100%;
+  background: rgba(0, 0, 0, 0.4);
+  z-index: 1;
+}
+
+/* Navigation */
+.landing-nav {
+  position: fixed;
+  top: 0;
+  left: 0;
+  width: 100%;
+  z-index: 10;
+  background: rgba(0,0,0,0.3);
+  backdrop-filter: blur(10px);
+}
+
+.landing-nav nav {
+  display: flex;
+  justify-content: space-between;
+  align-items: center;
+  padding: 1rem 2rem;
+}
+
+.logo-img {
+  height: 40px;
+  width: auto;
+}
+
+.nav-links {
+  display: flex;
+  align-items: center;
+  gap: 1rem;
+}
+
+.main-site-link,
+.coming-soon-badge {
+  padding: 0.5rem 1rem;
+  border-radius: 20px;
+  font-weight: 600;
+  text-decoration: none;
+  transition: all 0.3s ease;
+}
+
+.main-site-link {
+  background: linear-gradient(45deg, #D4AF37, #FFD700);
+  color: #4A148C;
+}
+
+.coming-soon-badge {
+  background: rgba(212, 175, 55, 0.2);
+  color: #D4AF37;
+  border: 1px solid rgba(212, 175, 55, 0.3);
+}
+
+/* Content Overlay - Left alignment for iPhone */
+.content-overlay {
+  position: relative;
+  z-index: 2;
+  min-height: 100vh;
+  display: flex;
+  flex-direction: column;
+  justify-content: center;
+  align-items: flex-start;
+  padding: 2rem;
+}
+
+/* Brand Header - Fixed positioning and size */
+.brand-header {
+  position: absolute;
+  top: 15%;
+  left: 50%;
+  transform: translateX(-50%);
+  text-align: center;
+  z-index: 3;
+  width: 100%;
+  max-width: 100vw;
+  padding: 0 2rem;
+  box-sizing: border-box;
+}
+
+.brand-title {
+  font-size: 2.8rem;
+  font-weight: 900;
+  color: #D4AF37;
+  text-shadow: 2px 2px 4px rgba(0,0,0,0.7);
+  margin-bottom: 0.5rem;
+  letter-spacing: 3px;
+  word-wrap: break-word;
+  overflow-wrap: break-word;
+}
+
+.brand-tagline {
+  font-size: 1.1rem;
+  color: rgba(255,255,255,0.95);
+  text-shadow: 1px 1px 3px rgba(0,0,0,0.8);
+  margin: 0;
+  font-weight: 300;
+}
+
+/* iPhone 16 Pro Mockup - Smaller and positioned left */
+.phone-mockup {
+  position: relative;
+  width: 280px;
+  height: 600px;
+  background: linear-gradient(145deg, #1d1d1f, #2d2d30);
+  border-radius: 45px;
+  padding: 6px;
+  box-shadow: 
+    0 20px 60px rgba(0,0,0,0.6),
+    inset 0 1px 0 rgba(255,255,255,0.1),
+    0 0 0 1px rgba(255,255,255,0.05);
+  z-index: 3;
+  margin-left: 5%;
+  margin-top: 2rem;
+}
+
+/* iPhone 16 Pro Dynamic Island - Scaled down */
+.phone-mockup::before {
+  content: '';
+  position: absolute;
+  top: 18px;
+  left: 50%;
+  transform: translateX(-50%);
+  width: 90px;
+  height: 28px;
+  background: #000;
+  border-radius: 14px;
+  z-index: 4;
+  box-shadow: inset 0 0 0 1px rgba(255,255,255,0.1);
+}
+
+/* iPhone 16 Pro Screen - Scaled down */
+.phone-screen {
+  width: 100%;
+  height: 100%;
+  background: #000; /* iOS dark mode */
+  border-radius: 39px;
+  padding: 45px 18px 30px 18px;
+  display: flex;
+  flex-direction: column;
+  justify-content: center;
+  overflow: hidden;
+  position: relative;
+  box-shadow: inset 0 0 0 1px rgba(255,255,255,0.1);
+}
+
+/* Status bar area (top safe area) - Scaled */
+.phone-screen::before {
+  content: '';
+  position: absolute;
+  top: 0;
+  left: 0;
+  right: 0;
+  height: 45px;
+  background: transparent;
+  z-index: 1;
+}
+
+/* System message container - Scaled */
+.system-message {
+  position: absolute;
+  top: 60px;
+  left: 18px;
+  right: 18px;
+  background: rgba(255,255,255,0.1);
+  backdrop-filter: blur(20px);
+  border-radius: 12px;
+  padding: 14px;
+  border: 1px solid rgba(255,255,255,0.15);
+  z-index: 2;
+}
+
+.system-message p {
+  color: white;
+  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
+  font-size: 13px;
+  font-weight: 400;
+  line-height: 1.3;
+  margin: 0;
+  text-align: center;
+  letter-spacing: -0.24px;
+}
+
+/* Live message styling */
+.system-message.live {
+  background: rgba(255,255,255,0.15);
+  border: 1px solid rgba(52, 199, 89, 0.3);
+  animation: systemPulse 3s ease-in-out infinite;
+}
+
+.system-message.live p {
+  font-weight: 500;
+}
+
+@keyframes systemPulse {
+  0%, 100% { 
+    background: rgba(255,255,255,0.15);
+    border-color: rgba(52, 199, 89, 0.3);
+  }
+  50% { 
+    background: rgba(255,255,255,0.2);
