+  align-items: center;
+  padding: 1rem 2rem;
+}
+
+.logo-img {
+  height: 40px;
+  width: auto;
+}
+
+.nav-links {
+  display: flex;
+  align-items: center;
+  gap: 1rem;
+}
+
+.main-site-link,
+.coming-soon-badge {
+  padding: 0.5rem 1rem;
+  border-radius: 20px;
+  font-weight: 600;
+  text-decoration: none;
+  transition: all 0.3s ease;
+}
+
+.main-site-link {
+  background: linear-gradient(45deg, #D4AF37, #FFD700);
+  color: #4A148C;
+}
+
+.coming-soon-badge {
+  background: rgba(212, 175, 55, 0.2);
+  color: #D4AF37;
+  border: 1px solid rgba(212, 175, 55, 0.3);
+}
+
+/* Content Overlay - Left alignment for iPhone */
+.content-overlay {
+  position: relative;
+  z-index: 2;
+  min-height: 100vh;
+  display: flex;
+  flex-direction: column;
+  justify-content: center;
+  align-items: flex-start;
+  padding: 2rem;
+}
+
+/* Brand Header - Fixed positioning and size */
+.brand-header {
+  position: absolute;
+  top: 15%;
+  left: 50%;
+  transform: translateX(-50%);
+  text-align: center;
+  z-index: 3;
+  width: 100%;
+  max-width: 100vw;
+  padding: 0 2rem;
+  box-sizing: border-box;
+}
+
+.brand-title {
+  font-size: 2.8rem;
+  font-weight: 900;
+  color: #D4AF37;
+  text-shadow: 2px 2px 4px rgba(0,0,0,0.7);
+  margin-bottom: 0.5rem;
+  letter-spacing: 3px;
+  word-wrap: break-word;
+  overflow-wrap: break-word;
+}
+
+.brand-tagline {
+  font-size: 1.1rem;
+  color: rgba(255,255,255,0.95);
+  text-shadow: 1px 1px 3px rgba(0,0,0,0.8);
+  margin: 0;
+  font-weight: 300;
+}
+
+/* iPhone 16 Pro Mockup - Smaller and positioned left */
+.phone-mockup {
+  position: relative;
+  width: 280px;
+  height: 600px;
+  background: linear-gradient(145deg, #1d1d1f, #2d2d30);
+  border-radius: 45px;
+  padding: 6px;
+  box-shadow: 
+    0 20px 60px rgba(0,0,0,0.6),
+    inset 0 1px 0 rgba(255,255,255,0.1),
+    0 0 0 1px rgba(255,255,255,0.05);
+  z-index: 3;
+  margin-left: 5%;
+  margin-top: 2rem;
+}
+
+/* iPhone 16 Pro Dynamic Island - Scaled down */
+.phone-mockup::before {
+  content: '';
+  position: absolute;
+  top: 18px;
+  left: 50%;
+  transform: translateX(-50%);
+  width: 90px;
+  height: 28px;
+  background: #000;
+  border-radius: 14px;
+  z-index: 4;
+  box-shadow: inset 0 0 0 1px rgba(255,255,255,0.1);
+}
+
+/* iPhone 16 Pro Screen - Scaled down */
+.phone-screen {
+  width: 100%;
+  height: 100%;
+  background: #000; /* iOS dark mode */
+  border-radius: 39px;
+  padding: 45px 18px 30px 18px;
+  display: flex;
+  flex-direction: column;
+  justify-content: center;
+  overflow: hidden;
+  position: relative;
+  box-shadow: inset 0 0 0 1px rgba(255,255,255,0.1);
+}
+
+/* Status bar area (top safe area) - Scaled */
+.phone-screen::before {
+  content: '';
+  position: absolute;
+  top: 0;
+  left: 0;
+  right: 0;
+  height: 45px;
+  background: transparent;
+  z-index: 1;
+}
+
+/* Form Container - Updated positioning without system message */
+.form-container {
+  display: block;
+  position: relative;
+  z-index: 3;
+  margin-top: 20px; /* Reduced since system message is removed */
+}
+
+.form-container h2 {
+  color: white;
+  text-align: center;
+  margin-bottom: 1.5rem;
+  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
+  font-size: 18px;
+  font-weight: 600;
+  letter-spacing: -0.45px;
+}
+
+.form-container input,
+.form-container select {
+  width: 100%;
+  padding: 12px;
+  margin-bottom: 10px;
+  border: none;
+  border-radius: 10px;
+  background: rgba(255,255,255,0.9);
+  color: #000;
+  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
+  font-size: 14px;
+  font-weight: 400;
+  box-sizing: border-box;
